import React, { useState, useEffect, useMemo, useCallback } from "react";
import DashboardLayout from "components/layout/DashboardLayout";
import { dbService } from "shared/services";
import { FilterBar, StatsGrid, DataTable, ActionDropdown, StatusToggle, FormInput, FormSelect, FormGroup, ExportDropdown } from "components/UI";
import { ViewDetailsModal, EditFormModal, CreateFormModal } from "components/common";
import { useFilters, useModal } from "shared/hooks";
import {
  validateParticipanteDocumentoUnico,
  validateFechaNacimiento,
  validateFechaIngreso,
  validateSedeExists
} from "shared/utils/validationUtils";
import {
  arrayToCSV,
  downloadCSV,
  calculateAge,
  formatDateForCSV,
  formatParticipantName,
  formatSede,
  normalizeStatus,
  formatGender
} from "shared/utils/exportUtils";
// import jsPDF from 'jspdf'; // Temporarily disabled - not available in Docker dev

const Participantes = React.memo(() => {
  const [participantes, setParticipantes] = useState([]); // Asegurar que siempre sea array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Use the useFilters hook for filter state management
  const { filters: filtros, setFilter, clearFilters } = useFilters({
    sede: "Todas",
    genero: "Todos",
    estado: "Todos",
    busqueda: ""
  });

  // Use the useModal hook for modal state management
  const viewModal = useModal();
  const editModal = useModal();
  const createModal = useModal();

  // Funci√≥n para cargar participantes (usando useCallback para evitar re-renders)
  const loadParticipantes = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('üîÑ Cargando participantes...');

      const result = await dbService.getParticipantes();
      console.log('üìä Resultado participantes:', result);
      console.log('üì¶ result.data:', result.data);
      console.log('üì¶ result.data type:', typeof result.data);
      console.log('üì¶ result.data isArray:', Array.isArray(result.data));

      if (result.error) {
        throw new Error(result.error.message || 'Error al cargar participantes');
      }

      // The API returns {data: {data: [...], error: null}, error: null}, so we need result.data.data
      const participantesData = Array.isArray(result.data?.data) ? result.data.data : [];
      console.log('üîÑ participantesData:', participantesData);
      console.log('üîÑ participantesData.length:', participantesData.length);
      setParticipantes(participantesData);
      console.log('‚úÖ Participantes cargados:', participantesData.length);
    } catch (err) {
      console.error('‚ùå Error cargando participantes:', err);
      setError(err.message || 'Error desconocido al cargar participantes');
      setParticipantes([]); // Asegurar que no quede undefined
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadParticipantes();
  }, [loadParticipantes]);

  // Funciones para manejar modales (memoized)
  const abrirModal = useCallback((tipo, participante) => {
    if (tipo === 'ver') {
      viewModal.openModal(null, participante);
    } else if (tipo === 'editar') {
      editModal.openModal(null, participante);
    } else if (tipo === 'crear') {
      createModal.openModal();
    }
  }, [viewModal, editModal, createModal]);

  const filteredParticipantes = useMemo(() => {
    // Asegurar que participantes siempre sea un array
    const safeParticipantes = Array.isArray(participantes) ? participantes : [];
    let filtered = safeParticipantes;

    if (filtros.sede !== "Todas") {
      filtered = filtered.filter(p => {
        const sedeNombre = p.sede?.direccion || p.sede || '';
        return sedeNombre.toLowerCase().includes(filtros.sede.toLowerCase());
      });
    }
    if (filtros.genero !== "Todos") {
      filtered = filtered.filter(p => p.genero === filtros.genero);
    }
    if (filtros.estado !== "Todos") {
      // Support both ACTIVO/INACTIVO and Activo/Inactivo
      const estadoUpper = filtros.estado.toUpperCase();
      filtered = filtered.filter(p => 
        p.estado === filtros.estado || 
        p.estado === estadoUpper ||
        (filtros.estado === 'Activo' && p.estado === 'ACTIVO') ||
        (filtros.estado === 'Inactivo' && p.estado === 'INACTIVO')
      );
    }
    if (filtros.busqueda) {
      const searchLower = filtros.busqueda.toLowerCase();
      filtered = filtered.filter(p => {
        const documento = (p.numero_documento || '').toLowerCase();

        return documento.includes(searchLower);
      });
    }
    return filtered;
  }, [participantes, filtros]);

  const toggleEstado = useCallback(async (id, newEstado) => {
    try {
      await dbService.updateParticipante(id, { estado: newEstado });
      // Refresh data
      const { data } = await dbService.getParticipantes();
      setParticipantes(data || []);
    } catch (err) {
      console.error('Error updating status:', err);
    }
  }, []);

  const handleActionClick = useCallback((action, participante) => {
    switch (action) {
      case 'ver':
        abrirModal('ver', participante);
        break;
      case 'editar':
        abrirModal('editar', participante);
        break;
      default:
        // No action
        break;
    }
  }, [abrirModal]);

  const handleExportPDF = useCallback(() => {
    // Create a new window for printing
    const printWindow = window.open('', '_blank');
    const currentDate = new Date().toLocaleDateString('es-ES');

    // Generate HTML content for PDF
    const htmlContent = `
      <!DOCTYPE html>
      <html>
        <head>
          <title>Lista de Participantes - ${currentDate}</title>
          <style>
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
              color: #333;
            }
            .header {
              text-align: center;
              margin-bottom: 30px;
              border-bottom: 2px solid #333;
              padding-bottom: 20px;
            }
            .header h1 {
              color: #2563eb;
              margin: 0;
              font-size: 24px;
            }
            .filters {
              margin-bottom: 20px;
              background: #f8f9fa;
              padding: 15px;
              border-radius: 5px;
            }
            .filters h3 {
              margin: 0 0 10px 0;
              color: #495057;
            }
            .filters p {
              margin: 5px 0;
              font-size: 14px;
            }
            table {
              width: 100%;
              border-collapse: collapse;
              margin-top: 20px;
            }
            th, td {
              border: 1px solid #ddd;
              padding: 8px 12px;
              text-align: left;
            }
            th {
              background-color: #f8f9fa;
              font-weight: bold;
              color: #495057;
            }
            tr:nth-child(even) {
              background-color: #f9f9f9;
            }
            .stats {
              margin-top: 30px;
              background: #e3f2fd;
              padding: 15px;
              border-radius: 5px;
            }
            .stats h3 {
              margin: 0 0 10px 0;
              color: #1976d2;
            }
            .stats p {
              margin: 5px 0;
              font-size: 14px;
            }
            @media print {
              body { margin: 0; }
              .no-print { display: none; }
            }
          </style>
        </head>
        <body>
          <div class="header">
            <h1>Lista de Participantes</h1>
            <p>Corporaci√≥n Todo por un Alma</p>
            <p>Fecha de generaci√≥n: ${currentDate}</p>
          </div>

          <div class="filters">
            <h3>Filtros aplicados:</h3>
            <p><strong>Sede:</strong> ${filtros.sede === 'Todas' ? 'Todas las sedes' : filtros.sede}</p>
            <p><strong>G√©nero:</strong> ${filtros.genero === 'Todos' ? 'Todos los g√©neros' : filtros.genero}</p>
            <p><strong>Estado:</strong> ${filtros.estado === 'Todos' ? 'Todos los estados' : filtros.estado}</p>
            ${filtros.busqueda ? `<p><strong>B√∫squeda:</strong> "${filtros.busqueda}"</p>` : ''}
          </div>

          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Edad</th>
                <th>G√©nero</th>
                <th>Tel√©fono</th>
                <th>Sede</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              ${filteredParticipantes.map(participante => `
                <tr>
                  <td>${formatParticipantName(participante)}</td>
                  <td>${calculateAge(participante.fecha_nacimiento) || 'N/A'}</td>
                  <td>${formatGender(participante.genero)}</td>
                  <td>${participante.telefono || 'N/A'}</td>
                  <td>${formatSede(participante.sede)}</td>
                  <td>${normalizeStatus(participante.estado)}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>

          <div class="stats">
            <h3>Estad√≠sticas:</h3>
            <p><strong>Total de participantes:</strong> ${filteredParticipantes.length}</p>
            <p><strong>Activos:</strong> ${filteredParticipantes.filter(p => {
              const upper = String(p.estado || '').toUpperCase();
              return upper === 'ACTIVO';
            }).length}</p>
            <p><strong>Inactivos:</strong> ${filteredParticipantes.filter(p => {
              const upper = String(p.estado || '').toUpperCase();
              return upper === 'INACTIVO';
            }).length}</p>
          </div>

          <script>
            window.onload = function() {
              window.print();
              setTimeout(function() {
                window.close();
              }, 1000);
            }
          </script>
        </body>
      </html>
    `;

    printWindow.document.write(htmlContent);
    printWindow.document.close();
  }, [filtros, filteredParticipantes]);

  const handleExportCSV = useCallback(() => {
    const headers = [
      { key: 'tipo_documento', label: 'Tipo Documento' },
      { key: 'numero_documento', label: 'N√∫mero Documento' },
      { key: 'nombres', label: 'Nombres' },
      { key: 'apellidos', label: 'Apellidos' },
      { key: 'nombre_completo', label: 'Nombre Completo' },
      { key: 'fecha_nacimiento', label: 'Fecha Nacimiento' },
      { key: 'edad', label: 'Edad' },
      { key: 'genero', label: 'G√©nero' },
      { key: 'telefono', label: 'Tel√©fono' },
      { key: 'sede', label: 'Sede' },
      { key: 'fecha_ingreso', label: 'Fecha Ingreso' },
      { key: 'estado', label: 'Estado' }
    ];

    const csvData = filteredParticipantes.map(participante => ({
      tipo_documento: participante.tipo_documento || 'N/A',
      numero_documento: participante.numero_documento || 'N/A',
      nombres: participante.nombres || 'N/A',
      apellidos: participante.apellidos || 'N/A',
      nombre_completo: formatParticipantName(participante),
      fecha_nacimiento: formatDateForCSV(participante.fecha_nacimiento),
      edad: calculateAge(participante.fecha_nacimiento) || 'N/A',
      genero: formatGender(participante.genero),
      telefono: participante.telefono || 'N/A',
      sede: formatSede(participante.sede),
      fecha_ingreso: formatDateForCSV(participante.fecha_ingreso),
      estado: normalizeStatus(participante.estado)
    }));

    // Generar estad√≠sticas
    const activosCount = filteredParticipantes.filter(p => {
      const upper = String(p.estado || '').toUpperCase();
      return upper === 'ACTIVO';
    }).length;

    const inactivosCount = filteredParticipantes.filter(p => {
      const upper = String(p.estado || '').toUpperCase();
      return upper === 'INACTIVO';
    }).length;

    const masculinoCount = filteredParticipantes.filter(p => p.genero === 'MASCULINO').length;
    const femeninoCount = filteredParticipantes.filter(p => p.genero === 'FEMENINO').length;

    const statistics = {
      'Total de Participantes': filteredParticipantes.length,
      'Participantes Activos': `${activosCount} (${((activosCount / filteredParticipantes.length) * 100).toFixed(1)}%)`,
      'Participantes Inactivos': `${inactivosCount} (${((inactivosCount / filteredParticipantes.length) * 100).toFixed(1)}%)`,
      'G√©nero Masculino': `${masculinoCount} (${((masculinoCount / filteredParticipantes.length) * 100).toFixed(1)}%)`,
      'G√©nero Femenino': `${femeninoCount} (${((femeninoCount / filteredParticipantes.length) * 100).toFixed(1)}%)`
    };

    // Preparar filtros aplicados
    const appliedFilters = {
      sede: filtros.sede !== 'Todas' ? filtros.sede : null,
      g√©nero: filtros.genero !== 'Todos' ? filtros.genero : null,
      estado: filtros.estado !== 'Todos' ? filtros.estado : null,
      b√∫squeda: filtros.busqueda || null
    };

    const csvContent = arrayToCSV(csvData, headers);

    const filename = `participantes_${new Date().toISOString().split('T')[0]}.csv`;
    downloadCSV(csvContent, filename);
  }, [filteredParticipantes, filtros]);

  if (loading) {
    return (
      <DashboardLayout title="Participantes" subtitle="Gesti√≥n de participantes" loading={true} loadingText="Cargando participantes..." />
    );
  }

  if (error) {
    return (
      <DashboardLayout title="Participantes" subtitle="Error al cargar datos" loading={false}>
        <div className="flex items-center justify-center h-screen">
          <div className="bg-red-50 border border-red-200 rounded-lg p-4">
            <p className="text-red-600">Error loading participants: {error}</p>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout title="Gesti√≥n de Participantes" subtitle="Administra los participantes de la fundaci√≥n" extraActions={
      <div className="flex space-x-3">
        <ExportDropdown
          onExportPDF={handleExportPDF}
          onExportCSV={handleExportCSV}
        />
        <button
          onClick={() => abrirModal('crear', null)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
        >
          <i className="fas fa-plus mr-2"></i>
          Nuevo Participante
        </button>
      </div>
    }>
      <section className="px-6 py-6">
        <FilterBar
          filters={[
            {
              type: 'select',
              name: 'sede',
              label: 'Sede',
              options: [
                { value: 'Todas', label: 'Todas las Sedes' },
                { value: 'Bello Principal', label: 'Bello Principal' },
                { value: 'Bello Campestre', label: 'Bello Campestre' },
                { value: 'Apartad√≥', label: 'Apartad√≥' }
              ]
            },
            {
              type: 'select',
              name: 'genero',
              label: 'G√©nero',
              options: [
                { value: 'Todos', label: 'Todos los G√©neros' },
                { value: 'MASCULINO', label: 'Masculino' },
                { value: 'FEMENINO', label: 'Femenino' }
              ]
            },
            {
              type: 'select',
              name: 'estado',
              label: 'Estado',
              options: [
                { value: 'Todos', label: 'Todos los Estados' },
                { value: 'Activo', label: 'Activo' },
                { value: 'Inactivo', label: 'Inactivo' }
              ]
            },
            {
              type: 'search',
              name: 'busqueda',
              label: 'B√∫squeda por Documento',
              placeholder: 'N√∫mero de documento...',
              debounceMs: 300
            }
          ]}
          values={filtros}
          onChange={setFilter}
          onClear={clearFilters}
          showClearButton={true}
        />

