    }
  }

  // Obtener configuraci√≥n de la API
  getApiConfig() {
    return {
      authBaseURL: AUTH_API_BASE_URL,
      hasToken: !!localStorage.getItem('authToken'),
      environment: process.env.NODE_ENV,
      isConfigured: !!AUTH_API_BASE_URL
    };
  }
}

