import React, { useState, useEffect, useMemo, useCallback } from "react";
import DashboardLayout from "components/layout/DashboardLayout";
import { dbService } from "shared/services";
import { useFilters, usePagination, useModal } from "shared/hooks";
import {
  LoadingSpinner,
  FilterBar,
  DataTable,
  Pagination,
  StatusToggle,
  ActionDropdown,
  FormModal,
  FormInput,
  FormSelect,
  FormTextarea,
  ExportDropdown
} from "components/UI";
import { validateMensualidadRelations } from "shared/utils/validationUtils";
import {
  createEnhancedCSV,
  downloadCSV,
  formatCurrencyForCSV,
  formatDateForCSV,
  normalizeStatus
} from "shared/utils/exportUtils";

// Custom component for combined month-year filter
const MonthYearSelect = ({ value, onChange, label = "Período" }) => {
  const months = useMemo(() => [
    { value: 1, label: 'Enero' },
    { value: 2, label: 'Febrero' },
    { value: 3, label: 'Marzo' },
    { value: 4, label: 'Abril' },
    { value: 5, label: 'Mayo' },
    { value: 6, label: 'Junio' },
    { value: 7, label: 'Julio' },
    { value: 8, label: 'Agosto' },
    { value: 9, label: 'Septiembre' },
    { value: 10, label: 'Octubre' },
    { value: 11, label: 'Noviembre' },
    { value: 12, label: 'Diciembre' }
  ], []);

  const years = useMemo(() => Array.from({ length: 11 }, (_, i) => 2020 + i), []);

  const options = useMemo(() => {
    const opts = [{ value: 'all', label: 'Todos' }];
    years.forEach(year => {
      months.forEach(month => {
        opts.push({
          value: `${month.value}-${year}`,
          label: `${month.label} ${year}`
        });
      });
    });
    return opts;
  }, [months, years]);

  return (
    <div className="w-full">
      <label className="block text-sm font-medium text-gray-700 mb-1">
        {label}
      </label>
      <select
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    </div>
  );
};

const Finance = React.memo(() => {
  const [mensualidades, setMensualidades] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [sedes, setSedes] = useState([]);
  const [acudientes, setAcudientes] = useState([]);
  const [filteredAcudientes, setFilteredAcudientes] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    participant_id: '',
    id_acudiente: '',
    mes: '',
    año: new Date().getFullYear(),
    valor: '',
    status: 'PAGADO',
    metodo_pago: 'TRANSFERENCIA',
    fecha_pago: '',
    observaciones: ''
  });

  // Use custom hooks
  const { filters, setFilter, clearFilters } = useFilters({
    periodo: 'all',
    sede: 'all',
    estado: 'all',
    busqueda: ''
  });
  const { isOpen: showModal, openModal: openModalHook, closeModal, modalData, updateModalData } = useModal();

  // Memoized form handlers to prevent unnecessary re-renders
  const handleFormDataChange = useCallback((field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  const months = useMemo(() => [
    { value: 1, label: 'Enero' },
    { value: 2, label: 'Febrero' },
    { value: 3, label: 'Marzo' },
    { value: 4, label: 'Abril' },
    { value: 5, label: 'Mayo' },
    { value: 6, label: 'Junio' },
    { value: 7, label: 'Julio' },
    { value: 8, label: 'Agosto' },
    { value: 9, label: 'Septiembre' },
    { value: 10, label: 'Octubre' },
    { value: 11, label: 'Noviembre' },
    { value: 12, label: 'Diciembre' }
  ], []);

  const years = useMemo(() => Array.from({ length: 11 }, (_, i) => 2020 + i), []);

  const getMonthLabel = useCallback((mes) => {
    const month = months.find(m => m.value === mes);
    return month ? month.label : mes.toString();
  }, [months]);



  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        setError(null);
        console.log('🔄 Cargando datos financieros...');

        const [mensRes, partsRes, sedesRes, acudRes] = await Promise.all([
          dbService.getMensualidades(),
          dbService.getParticipantes(),
          dbService.getSedes(),
          dbService.getAcudientes()
        ]);

        console.log('📊 Resultados crudos:', {
          mensRes: { data: mensRes.data, error: mensRes.error },
          partsRes: { data: partsRes.data, error: partsRes.error },
          sedesRes: { data: sedesRes.data, error: sedesRes.error },
          acudRes: { data: acudRes.data, error: acudRes.error }
        });

        // Asegurar que siempre sean arrays (API service ya extrae el nested data)
        const mensualidadesData = Array.isArray(mensRes.data) ? mensRes.data : (mensRes.data?.data ? mensRes.data.data : []);
        const participantesData = Array.isArray(partsRes.data) ? partsRes.data : (partsRes.data?.data ? partsRes.data.data : []);
        const sedesData = Array.isArray(sedesRes.data) ? sedesRes.data : (sedesRes.data?.data ? sedesRes.data.data : []);
        const acudientesData = Array.isArray(acudRes.data) ? acudRes.data : (acudRes.data?.data ? acudRes.data.data : []);

        console.log('🔍 Estructura de mensualidadesData:', {
          isArray: Array.isArray(mensualidadesData),
          length: mensualidadesData.length,
          firstItem: mensualidadesData[0] || 'No hay elementos',
          rawResponse: mensRes
        });

        setMensualidades(mensualidadesData);
        setParticipants(participantesData);
        setSedes(sedesData);
        setAcudientes(acudientesData);
        setFilteredAcudientes(acudientesData);

        console.log('✅ Datos cargados:', {
          mensualidades: mensualidadesData.length,
          participantes: participantesData.length,
          sedes: sedesData.length,
          acudientes: acudientesData.length
        });
      } catch (err) {
        console.error('❌ Error cargando datos financieros:', err);
        setError(err.message || 'Error desconocido al cargar datos');
        // Asegurar arrays vacíos en caso de error
        setMensualidades([]);
        setParticipants([]);
        setSedes([]);
        setAcudientes([]);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Filter acudientes when participant changes in form
  useEffect(() => {
    if (formData.participant_id) {
      const filtered = acudientes.filter(a => a.id_participante === parseInt(formData.participant_id));
      setFilteredAcudientes(filtered);
      // Reset acudiente selection if current selection is not valid for new participant
      if (formData.id_acudiente && !filtered.find(a => a.id_acudiente === parseInt(formData.id_acudiente))) {
        setFormData(prev => ({ ...prev, id_acudiente: '' }));
      }
    } else {
      setFilteredAcudientes(acudientes);
    }
  }, [formData.participant_id, acudientes, formData.id_acudiente]);

  const filteredMensualidades = useMemo(() => {
    // Asegurar que mensualidades siempre sea un array
    const safeMensualidades = Array.isArray(mensualidades) ? mensualidades : [];
    let filtered = safeMensualidades;

    console.log('🔍 Aplicando filtros:', {
      filtrosActivos: filters,
      mensualidadesTotal: safeMensualidades.length,
      mensualidadesMuestra: safeMensualidades.slice(0, 3) // Primeros 3 elementos para debug
    });

    if (filters.periodo !== 'all') {
      const [mes, año] = filters.periodo.split('-').map(Number);
      console.log('📅 Filtrando por período:', { mes, año });
      filtered = filtered.filter(m => m.mes === mes && m.año === año);
      console.log('📅 Después de filtro período:', filtered.length);
    }
    if (filters.sede !== 'all') {
      console.log('🏢 Filtrando por sede:', filters.sede);
      filtered = filtered.filter(m => m.sede_id === parseInt(filters.sede));
      console.log('🏢 Después de filtro sede:', filtered.length);
    }
    if (filters.estado !== 'all') {
      console.log('📊 Filtrando por estado:', filters.estado);
      filtered = filtered.filter(m => m.estado === filters.estado);
      console.log('📊 Después de filtro estado:', filtered.length);
    }
    if (filters.busqueda) {
      console.log('🔍 Filtrando por búsqueda:', filters.busqueda);
      filtered = filtered.filter(m =>
        (m.participant_documento || '').includes(filters.busqueda) ||
        (m.acudiente_documento || '').includes(filters.busqueda)
      );
      console.log('🔍 Después de filtro búsqueda:', filtered.length);
    }

    console.log('✅ Resultado final del filtrado:', {
      totalFiltrado: filtered.length,
      filtrosAplicados: Object.keys(filters).filter(k => filters[k] !== 'all' && filters[k] !== '')
    });

    return filtered;
  }, [mensualidades, filters]);

  // Use pagination hook
  const {
    currentPage,
    totalPages,
    paginatedData: paginatedMensualidades,
    setPage
  } = usePagination(filteredMensualidades, 10);

  const openModal = useCallback((mensualidad = null) => {
    if (mensualidad) {
      // Pre-fill form with existing data
      setFormData({
        participant_id: mensualidad.participante_id || mensualidad.participant_id,
        id_acudiente: mensualidad.id_acudiente || '',
        mes: mensualidad.mes,
        año: mensualidad.año,
        valor: mensualidad.valor || mensualidad.monto,
        status: mensualidad.estado || mensualidad.status,
        metodo_pago: mensualidad.metodo_pago || 'TRANSFERENCIA',
        fecha_pago: mensualidad.fecha_pago || '',
        observaciones: mensualidad.observaciones || ''
      });
      updateModalData(mensualidad);
    } else {
      setFormData({
        participant_id: '',
        id_acudiente: '',
        mes: '',
        año: new Date().getFullYear(),
        valor: '',
        status: 'PAGADO',
        metodo_pago: 'TRANSFERENCIA',
        fecha_pago: '',
        observaciones: ''
      });
      updateModalData(null);
    }
    openModalHook();
  }, [openModalHook, updateModalData]);

  const toggleStatus = useCallback(async (id, newStatus) => {
    try {
      await dbService.updateMensualidad(id, { estado: newStatus });
      // Refresh data
      const { data } = await dbService.getMensualidades();
      setMensualidades(data || []);
    } catch (err) {
      console.error('Error updating status:', err);
      // Optionally show error toast
    }
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();

    // Validate fecha_pago is required when status is PAGADO
    if (formData.status === 'PAGADO' && !formData.fecha_pago) {
      alert('La fecha de pago es requerida cuando el estado es PAGADO');
      return;
    }

    // Validate that participante and acudiente exist (if acudiente is provided)
    try {
      if (formData.id_acudiente) {
        const relationsValidation = await validateMensualidadRelations(
          parseInt(formData.participant_id),
          parseInt(formData.id_acudiente)
        );

        if (!relationsValidation.isValid) {
          alert(relationsValidation.error);
          return;
        }
      }
    } catch (err) {
      console.error('Error validating relations:', err);
      alert('Error al validar las relaciones: ' + (err.message || 'Error desconocido'));
      return;
    }

    const submitData = {
      participant_id: parseInt(formData.participant_id),
      id_acudiente: formData.id_acudiente ? parseInt(formData.id_acudiente) : null,
      mes: parseInt(formData.mes),
      año: parseInt(formData.año),
      monto: parseFloat(formData.valor),
      estado: formData.status,
      metodo_pago: formData.metodo_pago,
      fecha_pago: formData.status === 'PAGADO' ? formData.fecha_pago : null,
      observaciones: formData.observaciones || null
    };

    try {
      if (modalData?.id) {
        await dbService.updateMensualidad(modalData.id, submitData);
      } else {
        await dbService.createMensualidad(submitData);
      }
      closeModal();
      const { data } = await dbService.getMensualidades();
      setMensualidades(data || []);
    } catch (err) {
      console.error('Error saving payment:', err);
      alert('Error al guardar la mensualidad: ' + (err.message || 'Error desconocido'));
    }
  }, [formData, modalData, closeModal]);

  const handleExportPDF = useCallback(() => {
    const printWindow = window.open('', '_blank');
    const currentDate = new Date().toLocaleDateString('es-ES');

    const htmlContent = `
      <!DOCTYPE html>
      <html>
        <head>
          <title>Lista de Mensualidades - ${currentDate}</title>
          <style>
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
              color: #333;
            }
            .header {
              text-align: center;
              margin-bottom: 30px;
              border-bottom: 2px solid #333;
              padding-bottom: 20px;
            }
            .header h1 {
              color: #2563eb;
              margin: 0;
              font-size: 24px;
            }
            table {
              width: 100%;
              border-collapse: collapse;
              margin-top: 20px;
            }
            th, td {
              border: 1px solid #ddd;
              padding: 8px 12px;
              text-align: left;
            }
            th {
              background-color: #f8f9fa;
              font-weight: bold;
              color: #495057;
            }
            tr:nth-child(even) {
              background-color: #f9f9f9;
            }
            @media print {
              body { margin: 0; }
            }
          </style>
        </head>
        <body>
          <div class="header">
            <h1>Lista de Mensualidades</h1>
            <p>Corporación Todo por un Alma</p>
            <p>Fecha de generación: ${currentDate}</p>
          </div>

          <table>
            <thead>
              <tr>
                <th>Participante</th>
                <th>Acudiente</th>
                <th>Mes</th>
                <th>Año</th>
                <th>Valor</th>
                <th>Estado</th>
                <th>Método de Pago</th>
              </tr>
            </thead>
            <tbody>
              ${filteredMensualidades.map(m => `
                <tr>
                  <td>${m.participant_name || 'N/A'}</td>
                  <td>${m.acudiente_name || 'N/A'}</td>
                  <td>${getMonthLabel(m.mes)}</td>
                  <td>${m.año || 'N/A'}</td>
                  <td>$${(m.valor || m.monto || 0).toLocaleString()}</td>
                  <td>${normalizeStatus(m.estado || m.status)}</td>
                  <td>${m.metodo_pago || 'N/A'}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>

          <script>
            window.onload = function() {
              window.print();
              setTimeout(function() {
                window.close();
              }, 1000);
            }
          </script>
        </body>
      </html>
    `;

    printWindow.document.write(htmlContent);
    printWindow.document.close();
  }, [filteredMensualidades, getMonthLabel]);

  const handleExportCSV = useCallback(() => {
    const headers = [
      { key: 'id', label: 'ID' },
      { key: 'participant_name', label: 'Participante' },
      { key: 'participant_documento', label: 'Documento Participante' },
      { key: 'acudiente_name', label: 'Acudiente' },
      { key: 'acudiente_documento', label: 'Documento Acudiente' },
      { key: 'mes', label: 'Mes' },
      { key: 'año', label: 'Año' },
      { key: 'periodo', label: 'Período' },
      { key: 'valor', label: 'Valor' },
      { key: 'estado', label: 'Estado' },
      { key: 'metodo_pago', label: 'Método de Pago' },
      { key: 'fecha_pago', label: 'Fecha Pago' },
      { key: 'observaciones', label: 'Observaciones' }
    ];

    const csvData = filteredMensualidades.map(m => ({
      id: m.id || 'N/A',
      participant_name: m.participant_name || 'N/A',
      participant_documento: m.participant_documento || 'N/A',
      acudiente_name: m.acudiente_name || 'N/A',
      acudiente_documento: m.acudiente_documento || 'N/A',
      mes: getMonthLabel(m.mes),
      año: m.año || 'N/A',
      periodo: `${getMonthLabel(m.mes)} ${m.año}`,
      valor: formatCurrencyForCSV(m.valor || m.monto || 0),
      estado: normalizeStatus(m.estado || m.status),
      metodo_pago: m.metodo_pago === 'TRANSFERENCIA' ? 'Transferencia' :
                   m.metodo_pago === 'EFECTIVO' ? 'Efectivo' : 'N/A',
      fecha_pago: formatDateForCSV(m.fecha_pago),
      observaciones: m.observaciones || 'N/A'
    }));

    // Calcular estadísticas
    const pagadasCount = filteredMensualidades.filter(m =>
      (m.estado || m.status) === 'PAGADO'
    ).length;

    const pendientesCount = filteredMensualidades.filter(m =>
      (m.estado || m.status) === 'PENDIENTE'
    ).length;

    const totalMonto = filteredMensualidades.reduce((sum, m) =>
      sum + (parseFloat(m.valor || m.monto || 0)), 0
    );

    const montosPagados = filteredMensualidades
      .filter(m => (m.estado || m.status) === 'PAGADO')
      .reduce((sum, m) => sum + (parseFloat(m.valor || m.monto || 0)), 0);

    const montosPendientes = filteredMensualidades
      .filter(m => (m.estado || m.status) === 'PENDIENTE')
      .reduce((sum, m) => sum + (parseFloat(m.valor || m.monto || 0)), 0);

    // Contar métodos de pago
    const transferenciaCount = filteredMensualidades.filter(m =>
      m.metodo_pago === 'TRANSFERENCIA'
    ).length;

    const efectivoCount = filteredMensualidades.filter(m =>
      m.metodo_pago === 'EFECTIVO'
    ).length;

    const statistics = {
      'Total de Mensualidades': filteredMensualidades.length,
      'Mensualidades Pagadas': `${pagadasCount} (${((pagadasCount / filteredMensualidades.length) * 100).toFixed(1)}%)`,
      'Mensualidades Pendientes': `${pendientesCount} (${((pendientesCount / filteredMensualidades.length) * 100).toFixed(1)}%)`,
      'Monto Total': formatCurrencyForCSV(totalMonto),
      'Monto Pagado': formatCurrencyForCSV(montosPagados),
      'Monto Pendiente': formatCurrencyForCSV(montosPendientes),
      'Pagos por Transferencia': `${transferenciaCount} (${((transferenciaCount / filteredMensualidades.length) * 100).toFixed(1)}%)`,
      'Pagos en Efectivo': `${efectivoCount} (${((efectivoCount / filteredMensualidades.length) * 100).toFixed(1)}%)`
    };

    // Preparar filtros aplicados
    const appliedFilters = {};

    if (filters.periodo !== 'all') {
      const [mes, año] = filters.periodo.split('-');
      appliedFilters.período = `${getMonthLabel(parseInt(mes))} ${año}`;
    }

    if (filters.sede !== 'all') {
      const sede = sedes.find(s => s.id.toString() === filters.sede);
      appliedFilters.sede = sede?.nombre || filters.sede;
    }

    if (filters.estado !== 'all') {
      appliedFilters.estado = filters.estado === 'PAGADO' ? 'Pagada' :
                              filters.estado === 'PENDIENTE' ? 'Pendiente' :
                              filters.estado;
    }

    if (filters.busqueda) {
      appliedFilters.búsqueda = filters.busqueda;
    }

    const csvContent = createEnhancedCSV({
      title: 'Reporte de Mensualidades',
      data: csvData,
      headers,
      metadata: {
        generatedBy: 'Sistema de Gestión Financiera'
      },
      statistics,
      filters: appliedFilters
    });

    const filename = `mensualidades_${new Date().toISOString().split('T')[0]}.csv`;
    downloadCSV(csvContent, filename);
  }, [filteredMensualidades, getMonthLabel, filters, sedes]);

  if (loading) {
    return (
      <DashboardLayout
        title="Mensualidades"
        subtitle="Cargando mensualidades..."
        loading={true}
      >
        <LoadingSpinner />
      </DashboardLayout>
    );
  }

  if (error) {
    return (
      <DashboardLayout
        title="Mensualidades"
        subtitle="Error al cargar datos"
        loading={false}
      >
        <div className="flex items-center justify-center h-screen">
          <div className="bg-red-50 border border-red-200 rounded-lg p-4">
            <p className="text-red-600">Error loading payments: {error}</p>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout
      title="Mensualidades"
      subtitle="Pagadas, pendientes y vencidas"
      extraActions={
        <div className="flex space-x-3">
          <ExportDropdown
            onExportPDF={handleExportPDF}
            onExportCSV={handleExportCSV}
          />
          <button onClick={() => openModal()} className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
            <i className="fas fa-plus mr-2"></i>Nueva Mensualidad
          </button>
        </div>
      }
    >
      <section className="px-4 md:px-6 py-4 md:py-6">
            <FilterBar
              filters={[
                {
                  type: 'custom',
                  name: 'periodo',
                  component: MonthYearSelect,
                  props: { label: 'Período' }
                },
                {
                  type: 'select',
                  name: 'sede',
                  label: 'Sede',
                  placeholder: 'Todas',
                  options: [
                    { value: 'all', label: 'Todas' },
                    ...sedes.map(s => ({ value: s.id.toString(), label: s.nombre }))
                  ]
                },
                {
                  type: 'select',
                  name: 'estado',
                  label: 'Estado',
                  placeholder: 'Todos',
                  options: [
                    { value: 'all', label: 'Todos' },
                    { value: 'PAGADO', label: 'Pagada' },
                    { value: 'PENDIENTE', label: 'Pendiente' }
                  ]
                },
                {
                  type: 'search',
                  name: 'busqueda',
                  label: 'Búsqueda por Documento',
                  placeholder: 'Número de documento...'
                }
              ]}
              values={filters}
              onChange={setFilter}
              onClear={clearFilters}
              showClearButton={true}
            />
            <div className="mt-6">
              <DataTable
                data={paginatedMensualidades}
                columns={[
                  {
                    key: 'participant_name',
                    header: 'Participante',
                    render: (row) => (
                      <span className="font-medium text-gray-900">{row.participant_name}</span>
                    )
                  },
                  {
                    key: 'acudiente_name',
                    header: 'Acudiente',
                    render: (row) => (
                      <span className="text-gray-700">{row.acudiente_name || 'N/A'}</span>
                    )
                  },
                  {
                    key: 'mes',
                    header: 'Mes',
                    render: (row) => getMonthLabel(row.mes)
                  },
                  {
                    key: 'valor',
                    header: 'Cantidad',
                    render: (row) => `$${(row.valor || row.monto || 0).toLocaleString()}`
                  },
                  {
                    key: 'metodo_pago',
                    header: 'Método de Pago',
                    render: (row) => (
                      <span className="text-sm text-gray-600">
                        {row.metodo_pago === 'TRANSFERENCIA' ? 'Transferencia' : 
                         row.metodo_pago === 'EFECTIVO' ? 'Efectivo' : 'N/A'}
                      </span>
                    )
                  },
                  {
                    key: 'status',
                    header: 'Estado',
                    render: (row) => (
                      <StatusToggle
                        currentStatus={row.status || row.estado}
                        statuses={[
                          { value: 'PAGADO', label: 'PAGADO', variant: 'success' },
                          { value: 'PENDIENTE', label: 'PENDIENTE', variant: 'danger' }
                        ]}
                        onChange={(newStatus) => toggleStatus(row.id, newStatus)}
                      />
                    )
                  },
                  {
                    key: 'observaciones',
                    header: 'Observaciones',
                    render: (row) => (
                      <span className="text-sm text-gray-600 truncate max-w-xs block" title={row.observaciones || ''}>
                        {row.observaciones ? 
                          (row.observaciones.length > 30 ? 
                            row.observaciones.substring(0, 30) + '...' : 
                            row.observaciones) : 
                          '-'}
                      </span>
                    )
                  },
                  {
                    key: 'actions',
                    header: 'Acciones',
                    render: (row) => (
                      <ActionDropdown
                        actions={[
                          {
                            label: 'Ver',
                            icon: 'fas fa-eye',
                            onClick: () => openModal(row)
                          },
                          {
                            label: 'Editar',
                            icon: 'fas fa-edit',
                            onClick: () => openModal(row)
                          }
                        ]}
                      />
                    )
                  }
                ]}
                keyExtractor={(row) => row.id}
                loading={loading}
                emptyState={
                  <div className="text-center py-8 text-gray-500">
                    <div className="mb-4">
                      <strong>No hay mensualidades que mostrar</strong>
                    </div>
                    <div className="text-sm text-gray-400 space-y-1">
                      <div>📊 Total mensualidades en BD: {mensualidades.length}</div>
                      <div>🔍 Después de filtros: {filteredMensualidades.length}</div>
                      <div>⚙️ Filtros activos: {Object.keys(filters).filter(k => filters[k] !== 'all' && filters[k] !== '').length > 0 ?
                        Object.keys(filters).filter(k => filters[k] !== 'all' && filters[k] !== '').join(', ') :
                        'Ninguno'}</div>
                      <div>🔧 Estado de carga: {loading ? 'Cargando...' : 'Completado'}</div>
                      {error && <div className="text-red-400">❌ Error: {error}</div>}
                    </div>
                  </div>
                }
              />
              {totalPages > 1 && (
                <Pagination
                  currentPage={currentPage}
                  totalPages={totalPages}
                  onPageChange={setPage}
                  itemsPerPage={10}
                  totalItems={filteredMensualidades.length}
                  showInfo={true}
                  className="mt-4"
                />
              )}
            </div>
          </section>

          {/* Modal */}
          <FormModal
            isOpen={showModal}
            onClose={closeModal}
            title={modalData ? 'Ver/Editar Mensualidad' : 'Nueva Mensualidad'}
            onSubmit={handleSubmit}
            submitLabel="Guardar"
            size="lg"
          >
            <div className="space-y-4">
              <FormSelect
                label="Participante"
                name="participant_id"
                value={formData.participant_id}
                onChange={(value) => handleFormDataChange('participant_id', value)}
                options={participants.map(p => ({
                  value: p.id,
                  label: `${p.nombres} ${p.apellidos} - ${p.documento}`
                }))}
                placeholder="Seleccionar Participante"
                required
              />

              <FormSelect
                label="Acudiente"
                name="id_acudiente"
                value={formData.id_acudiente}
                onChange={(value) => handleFormDataChange('id_acudiente', value)}
                options={filteredAcudientes.map(a => ({
                  value: a.id_acudiente || a.id,
                  label: `${a.nombres} ${a.apellidos} - ${a.parentesco}`
                }))}
                placeholder="Seleccionar Acudiente"
                disabled={!formData.participant_id}
              />

              <div className="grid grid-cols-2 gap-4">
                <FormSelect
                  label="Mes"
                  name="mes"
                  value={formData.mes}
                  onChange={(value) => handleFormDataChange('mes', value)}
                  options={months.map(m => ({
                    value: m.value,
                    label: m.label
                  }))}
                  placeholder="Seleccionar Mes"
                  required
                />

                <FormSelect
                  label="Año"
                  name="año"
                  value={formData.año}
                  onChange={(value) => handleFormDataChange('año', value)}
                  options={years.map(y => ({
                    value: y,
                    label: y.toString()
                  }))}
                  required
                />
              </div>

              <FormInput
                label="Valor"
                name="valor"
                type="number"
                value={formData.valor}
                onChange={(value) => handleFormDataChange('valor', value)}
                placeholder="Valor"
                required
              />

              <div className="grid grid-cols-2 gap-4">
                <FormSelect
                  label="Estado"
                  name="status"
                  value={formData.status}
                  onChange={(value) => handleFormDataChange('status', value)}
                  options={[
                    { value: 'PAGADO', label: 'Pagada' },
                    { value: 'PENDIENTE', label: 'Pendiente' }
                  ]}
                  required
                />

                <FormSelect
                  label="Método de Pago"
                  name="metodo_pago"
                  value={formData.metodo_pago}
                  onChange={(value) => handleFormDataChange('metodo_pago', value)}
                  options={[
                    { value: 'TRANSFERENCIA', label: 'Transferencia' },
                    { value: 'EFECTIVO', label: 'Efectivo' }
                  ]}
                  required
                />
              </div>

              {formData.status === 'PAGADO' && (
                <FormInput
                  label="Fecha de Pago"
                  name="fecha_pago"
                  type="date"
                  value={formData.fecha_pago}
                  onChange={(value) => handleFormDataChange('fecha_pago', value)}
                  required={formData.status === 'PAGADO'}
                />
              )}

              <FormTextarea
                label="Observaciones"
                name="observaciones"
                value={formData.observaciones}
                onChange={(value) => handleFormDataChange('observaciones', value)}
                placeholder="Observaciones adicionales..."
                rows={3}
              />

              {modalData && (
                <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                  <h4 className="font-semibold text-gray-700 mb-2">Información Completa</h4>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    <div>
                      <span className="text-gray-600">Participante:</span>
                      <span className="ml-2 font-medium">{modalData.participant_name}</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Acudiente:</span>
                      <span className="ml-2 font-medium">{modalData.acudiente_name || 'N/A'}</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Método de Pago:</span>
                      <span className="ml-2 font-medium">
                        {modalData.metodo_pago === 'TRANSFERENCIA' ? 'Transferencia' : 
                         modalData.metodo_pago === 'EFECTIVO' ? 'Efectivo' : 'N/A'}
                      </span>
                    </div>
                    <div>
                      <span className="text-gray-600">Fecha de Pago:</span>
                      <span className="ml-2 font-medium">
                        {modalData.fecha_pago ? new Date(modalData.fecha_pago).toLocaleDateString() : 'N/A'}
                      </span>
                    </div>
                    {modalData.observaciones && (
                      <div className="col-span-2">
                        <span className="text-gray-600">Observaciones:</span>
                        <p className="ml-2 mt-1 text-gray-700">{modalData.observaciones}</p>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </FormModal>
        </DashboardLayout>
      );
    });

export default Finance;
